import React, { useState, useEffect } from "react";
import axios from "axios";

const API_BASE = "http://127.0.0.1:8000";

function App() {
  const [bubbles, setBubbles] = useState([]);
  const [formData, setFormData] = useState({
    product_name: "",
    adjustment_type: "inbound",
    quantity: 0,
    notes: "",
  });

  // Fetch all bubbles
  const fetchBubbles = async () => {
    const res = await axios.get(`${API_BASE}/bubbles`);
    setBubbles(res.data);
  };

  // Handle form changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handle form submit
  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post(`${API_BASE}/bubbles`, formData);
    setFormData({ product_name: "", adjustment_type: "inbound", quantity: 0, notes: "" });
    fetchBubbles();
  };

  useEffect(() => {
    fetchBubbles();
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h1>Bubble Inventory Tracker</h1>
      <form onSubmit={handleSubmit} style={{ marginBottom: "20px" }}>
        <input
          name="product_name"
          value={formData.product_name}
          onChange={handleChange}
          placeholder="Product Name"
          required
        />
        <select name="adjustment_type" value={formData.adjustment_type} onChange={handleChange}>
          <option value="inbound">Inbound</option>
          <option value="outbound">Outbound</option>
          <option value="adjustment">Adjustment</option>
        </select>
        <input
          name="quantity"
          type="number"
          value={formData.quantity}
          onChange={handleChange}
          placeholder="Quantity"
          required
        />
        <input
          name="notes"
          value={formData.notes}
          onChange={handleChange}
          placeholder="Notes"
        />
        <button type="submit">Add Bubble</button>
      </form>

      <h2>All Bubbles</h2>
      {bubbles.length === 0 && <p>No bubbles found.</p>}
      {bubbles.map((b) => (
        <div
          key={b.id}
          style={{
            border: "1px solid #ccc",
            padding: "10px",
            marginBottom: "5px",
          }}
        >
          <strong>{b.product_name}</strong> ({b.adjustment_type}) - {b.quantity}
          <br />
          <small>{b.notes || "No notes"} | Created: {b.created_at}</small>
        </div>
      ))}
    </div>
  );
}

export default App;
